import React,{useState} from 'react'

function LoginPage() {
    const [username, setUsername] = useState(null);
    const [password,setPassword] = useState(null);

    const hundleChage =(e)=>{
        const {id , value} = e.target;

        if(id === "username"){
            setUsername(value);
        }
        if(id === "password"){
            setPassword(value);
        }
       
    }

    const handleSubmit =()=>{
        console.log(username,password);
        
    }
  







  return (
    <> <div id="login-box">
    <div className="left">
      <h1>Login </h1>
      <input type="text" name="username" placeholder="Username" id='username' onChange={hundleChage} />
      <input type="password" name="password" placeholder="Password" id='password' onChange={hundleChage} />
      <input type="submit" name="signup_submit" defaultValue="Login" onSubmit={handleSubmit} />
    </div>
    <div className="right" >
      <span className="loginwith">Sign in with<br />social network</span>
      <button className="social-signin facebook">Log in with facebook</button>
      <button className="social-signin twitter">Log in with Twitter</button>
      <button className="social-signin google">Log in with Google+</button>
    </div>
    <div className="or">OR</div>
  </div>
</> 
  )
}

export default LoginPage
