import React,{useState} from 'react'
import { Redirect } from 'react-router-dom';

function LoginPage() {
    const [username, setUsername] = useState(null);
    const [password,setPassword] = useState(null);
    const [error,setError] = useState(null)

    const hundleChage =(e)=>{
        const {id , value} = e.target;

        if(id === "username"){
            setUsername(value);
        }
        if(id === "password"){
            setPassword(value);
        }
       
    }

    const handleSubmit =()=>{
        let olddata = localStorage.getItem('registerUser')
        let oldArr = JSON.parse(olddata)

        oldArr.map(arr=>{
            if(username.length > 0 && password.length > 0){
                if (arr.email == username && (arr.password == this.state.password)) {
                    let user = username;
                 //   history.push({ pathname: "/welcome", user: this.state.name });
                  }else{
                    setError('Please check your email or password')
                  }
            }
        })      
    }
  







  return (
    <> <div id="login-box">
    <div className="left">
      <h1>Login </h1>
      <input type="text" name="username" placeholder="Username" id='username' onChange={hundleChage} />
      <input type="password" name="password" placeholder="Password" id='password' onChange={hundleChage} />
      <input type="submit" name="signup_submit" defaultValue="Login" onClick={handleSubmit} />
    </div>
    <div className="right" >
      <span className="loginwith">Sign in with<br />social network</span>
      <button className="social-signin facebook">Log in with facebook</button>
      <button className="social-signin twitter">Log in with Twitter</button>
      <button className="social-signin google">Log in with Google+</button>
    </div>
    <div className="or">OR</div>
  </div>
</> 
  )
}

export default LoginPage
